INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Ação', 'https://www.example.com/image1.jpg', 'Lute contra inimigos em um mundo de fantasia épico, usando habilidades e armas poderosas para salvar o reino. Descubra segredos antigos, lute contra chefes épicos e torne-se o herói que o mundo precisa!', 'PC, Xbox, PS4', 9.1, 'Salve o reino neste emocionante jogo de ação!', 'Heróis do Reino', 2022);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Aventura', 'https://www.example.com/image2.jpg', 'Explore um mundo vasto e cheio de vida, repleto de mistérios e aventuras. Conheça personagens interessantes, desvende segredos ocultos e faça escolhas que afetam o mundo ao seu redor.', 'PC, Xbox, PS5', 9.5, 'Descubra o mundo em um jogo de aventura épico!', 'Exploradores do Mundo', 2023);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Esporte', 'https://www.example.com/image3.jpg', 'Jogue futebol com seus amigos em partidas emocionantes e competitivas, usando habilidades e estratégias para vencer a partida. Personalize seus jogadores, crie táticas únicas e torne-se o melhor time do mundo!', 'PC, Xbox, PS4', 8.2, 'Vença a competição em um jogo de futebol emocionante!', 'Super Time de Futebol', 2021);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('RPG', 'https://www.example.com/image4.jpg', 'Crie seu próprio personagem e explore um mundo de fantasia cheio de perigos e aventuras. Lute contra inimigos poderosos, colete tesouros raros e desbloqueie habilidades únicas em sua jornada para se tornar um herói lendário.', 'PC, Xbox, PS5', 9.7, 'Torne-se um herói lendário em um jogo de RPG épico!', 'Aventuras Épicas', 2023);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Corrida', 'https://www.example.com/image5.jpg', 'Corra em pistas emocionantes em todo o mundo, usando carros rápidos e habilidades únicas para vencer as corridas. Personalize seus carros, desafie outros jogadores em partidas multiplayer e torne-se o melhor piloto do mundo!', 'PC, Xbox, PS4', 8.5, 'Vença a corrida em um jogo de corrida emocionante!', 'Pilotos de Velocidade', 2021);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Estratégia', 'https://www.example.com/image6.jpg', 'Crie e gere seu próprio império em um jogo de estratégia épico, usando habilidades de gerenciamento e planejamento para construir cidades, conquistar territórios e derrotar inimigos. Desenvolva tecnologias avançadas, treine um exército poderoso e torne-se o líder supremo!', 'PC, Xbox, PS5', 9.9, 'Construa seu império em um jogo de estratégia épico!', 'Imperium', 2022);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Simulação', 'https://www.example.com/image7.jpg', 'Gerencie sua própria fazenda em um mundo aberto cheio de oportunidades e desafios. Plante culturas, cuide dos animais, venda seus produtos e expanda sua propriedade para se tornar o fazendeiro mais bem sucedido da região!', 'PC, Xbox, PS4', 8.8, 'Gerencie sua própria fazenda em um jogo de simulação emocionante!', 'Fazenda dos Sonhos', 2021);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Tiro', 'https://www.example.com/image8.jpg', 'Lute contra terroristas em todo o mundo, usando armas e habilidades especiais para completar missões perigosas e salvar o mundo. Jogue em modo singleplayer ou multiplayer, personalize seu equipamento e torne-se o melhor soldado do mundo!', 'PC, Xbox, PS5', 9.3, 'Salve o mundo em um jogo de tiro emocionante!', 'Operações Especiais', 2023);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Luta', 'https://www.example.com/image9.jpg', 'Lute contra outros jogadores em partidas intensas de luta, usando habilidades e estratégias para vencer seus oponentes. Escolha seu personagem favorito, personalize seu estilo de luta e torne-se o melhor lutador do mundo!', 'PC, Xbox, PS4', 8.6, 'Vença a luta em um jogo de luta emocionante!', 'Lutadores do Mundo', 2022);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Quebra-Cabeça', 'https://www.example.com/image10.jpg', 'Resolva quebra-cabeças desafiadores em um mundo mágico cheio de mistérios e aventuras. Use seu cérebro para desbloquear segredos ocultos, coletar tesouros raros e salvar o mundo dos inimigos malvados!', 'PC, Xbox, PS5', 9.8, 'Resolva quebra-cabeças em um jogo mágico e emocionante!', 'Puzzles Mágicos', 2023);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Ação', 'https://www.example.com/image11.jpg', 'Enfrente hordas de zumbis em um mundo pós-apocalíptico, usando armas e equipamentos para sobreviver. Construa abrigos, busque suprimentos e junte-se a outros jogadores para combater ameaças ainda maiores.', 'PC, Xbox, PS4', 8.5, 'Sobreviva ao apocalipse zumbi em um jogo de ação emocionante!', 'Zona Morta', 2021);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Estratégia', 'https://www.example.com/image12.jpg', 'Construa sua própria empresa em um jogo de simulação empresarial desafiador. Gerencie finanças, recursos humanos e estratégias de marketing para alcançar o sucesso e se tornar o líder de mercado.', 'PC, Xbox, PS5', 9.1, 'Construa sua própria empresa em um jogo de estratégia empresarial emocionante!', 'Império Empresarial', 2023);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Aventura', 'https://www.example.com/image13.jpg', 'Explore uma ilha misteriosa cheia de tesouros escondidos, ruínas antigas e segredos perigosos. Sobreviva a animais selvagens, inimigos hostis e condições climáticas extremas para descobrir os mistérios da ilha.', 'PC, Xbox, PS4', 8.9, 'Explore uma ilha misteriosa em um jogo de aventura emocionante!', 'Ilha dos Tesouros', 2022);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('RPG', 'https://www.example.com/image14.jpg', 'Crie seu próprio personagem e embarque em uma jornada épica de aventura e perigo. Lute contra monstros, complete missões e evolua seu personagem para se tornar o herói lendário do mundo!', 'PC, Xbox, PS5', 9.7, 'Embarque em uma jornada épica em um jogo de RPG emocionante!', 'Heróis da Lenda', 2023);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Corrida', 'https://www.example.com/image15.jpg', 'Corra em pistas emocionantes em todo o mundo, usando carros poderosos e habilidades de direção para vencer seus oponentes. Personalize seu carro, desbloqueie novas pistas e torne-se o melhor piloto de corrida do mundo!', 'PC, Xbox, PS4', 8.3, 'Corra em pistas emocionantes em um jogo de corrida desafiador!', 'Velocidade Extrema', 2022);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Esporte', 'https://www.example.com/image16.jpg', 'Assuma o controle de seu time favorito e lute por vitórias em uma variedade de esportes emocionantes, incluindo futebol, basquete, beisebol e muito mais. Jogue contra jogadores de todo o mundo e torne-se o campeão mundial!', 'PC, Xbox, PS5', 8.8, 'Assuma o controle de seu time favorito em um jogo de esportes desafiador!', 'Esportes Mundiais', 2021);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Ação-Aventura', 'https://www.example.com/image17.jpg', 'Embarque em uma aventura emocionante através de um mundo mágico e perigoso, cheio de monstros, magia e segredos antigos. Explore ruínas antigas, lute contra chefes poderosos e descubra a verdade sobre a maldição que ameaça o mundo.', 'PC, Xbox, PS4', 9.4, 'Embarque em uma aventura emocionante em um jogo de ação e aventura mágico!', 'Lendas do Reino Mágico', 2022);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Estratégia em tempo real', 'https://www.example.com/image18.jpg', 'Comande seu exército em batalhas épicas em um mundo de fantasia. Construa fortalezas, treine soldados e lute contra inimigos poderosos em uma campanha emocionante para salvar o reino.', 'PC', 8.6, 'Comande seu exército em batalhas épicas em um jogo de estratégia em tempo real emocionante!', 'Reino em Guerra', 2021);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Simulação', 'https://www.example.com/image19.jpg', 'Construa sua cidade dos sonhos em um jogo de simulação desafiador. Gerencie recursos, construa prédios e infraestrutura, e lide com problemas como poluição e desastres naturais para tornar sua cidade a melhor do mundo!', 'PC', 9.2, 'Construa sua cidade dos sonhos em um jogo de simulação emocionante!', 'Cidade Perfeita', 2022);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Luta', 'https://www.example.com/image20.jpg', 'Escolha seu lutador favorito e lute contra oponentes poderosos em uma variedade de arenas em todo o mundo. Use habilidades especiais, combos poderosos e estratégias de luta para se tornar o campeão mundial!', 'PC, Xbox, PS4', 8.7, 'Escolha seu lutador favorito e lute em um jogo de luta emocionante!', 'Lutadores Mundiais', 2021);
INSERT INTO tb_game (genre, img_url, long_description, platforms, score, short_description, title, game_year) VALUES ('Corrida', 'https://www.example.com/image20.jpg', 'Pilote carros esportivos de última geração em pistas emocionantes em todo o mundo. Ajuste seu carro, domine a condução e lute pelo primeiro lugar em corridas épicas contra jogadores de todo o mundo!', 'PC, Xbox, PS5', 8.9, 'Pilote carros esportivos de última geração em um jogo de corrida emocionante!', 'Pistas Perigosas', 2021);
